    public static UserEntity toUserEntity(UserDTOReq request) {
        UserEnum userEnum = UserEnum.valueOf(request.userEnum());
        return UserEntity.builder()
                .name(request.name())
                .email(request.email())
                .password(request.password())
                .balance(request.balance())
                .userEnum(userEnum)
                .build();
    }

}

